import axios from 'axios'

const url="http://127.0.0.1:5000/rest"

export const all=async()=>{
    const t = await axios.get(`${url}/`)
    return t;
}

export const add=async(object)=>{
    const t = await axios.post(`${url}/`,object)
    return t;
}

export const read=async(key)=>{
    await axios.get(`${url}/`)
}
